import React, { useState, useEffect, useRef } from "react";
import { 
  FaReact, FaJs, FaHtml5, FaCss3Alt, FaNodeJs, FaDatabase, 
  FaGit, FaDocker, FaFigma, FaPython, FaJava, FaAws,
  FaCode, FaPalette, FaRocket, FaUsers, FaLightbulb, FaTrophy
} from "react-icons/fa";
import { 
  SiLeetcode, SiCodeforces, SiCodechef, SiGeeksforgeeks, SiTryhackme
} from "react-icons/si";
import "../styles/Skills.css";

export default function Skills() {
  const [activeCategory, setActiveCategory] = useState('frontend');
  const [isVisible, setIsVisible] = useState(false);

  const skillCategories = [
    { id: 'frontend', label: 'Frontend', icon: FaCode },
    { id: 'backend', label: 'Backend', icon: FaRocket },
    { id: 'design', label: 'Design', icon: FaPalette },
    { id: 'tools', label: 'Tools', icon: FaUsers },
    { id: 'profiles', label: 'Coding Profiles', icon: FaTrophy }
  ];

  const skills = {
    frontend: [
      { name: 'React', level: 90, icon: FaReact, color: '#61DAFB' },
      { name: 'JavaScript', level: 85, icon: FaJs, color: '#F7DF1E' },
      { name: 'HTML5', level: 95, icon: FaHtml5, color: '#E34F26' },
      { name: 'CSS3', level: 88, icon: FaCss3Alt, color: '#1572B6' },
      { name: 'TypeScript', level: 75, icon: FaCode, color: '#3178C6' },
      { name: 'Next.js', level: 70, icon: FaCode, color: '#000000' }
    ],
    backend: [
      { name: 'Node.js', level: 80, icon: FaNodeJs, color: '#339933' },
      { name: 'Python', level: 75, icon: FaPython, color: '#3776AB' },
      { name: 'Java', level: 70, icon: FaJava, color: '#ED8B00' },
      { name: 'MongoDB', level: 75, icon: FaDatabase, color: '#47A248' },
      { name: 'PostgreSQL', level: 70, icon: FaDatabase, color: '#336791' },
      { name: 'AWS', level: 65, icon: FaAws, color: '#FF9900' }
    ],
    design: [
      { name: 'Figma', level: 85, icon: FaFigma, color: '#F24E1E' },
      { name: 'UI/UX Design', level: 80, icon: FaPalette, color: '#6366F1' },
      { name: 'Responsive Design', level: 90, icon: FaCode, color: '#10B981' },
      { name: 'Prototyping', level: 75, icon: FaLightbulb, color: '#F59E0B' },
      { name: 'Design Systems', level: 70, icon: FaCode, color: '#8B5CF6' }
    ],
    tools: [
      { name: 'Git', level: 85, icon: FaGit, color: '#F05032' },
      { name: 'Docker', level: 70, icon: FaDocker, color: '#2496ED' },
      { name: 'VS Code', level: 90, icon: FaCode, color: '#007ACC' },
      { name: 'Postman', level: 80, icon: FaCode, color: '#FF6C37' },
      { name: 'Jest', level: 75, icon: FaCode, color: '#C21325' }
    ]
  };

  const codingProfiles = [
    { name: 'LeetCode', subtext: '300+ problems solved', icon: SiLeetcode, color: '#FFA116', url: 'https://leetcode.com/u/the10thvishnu/' },
    { name: 'CodeForces', subtext: 'Current Rating: 1020', icon: SiCodeforces, color: '#1F8ACB', url: 'https://codeforces.com/profile/your_handle' },
    { name: 'CodeChef', subtext: 'Current Rating: 1409 · Div 3', icon: SiCodechef, color: '#5B4638', url: 'https://www.codechef.com/users/your_username' },
    { name: 'GeeksForGeeks', subtext: 'Practice & Articles', icon: SiGeeksforgeeks, color: '#0F9D58', url: 'https://auth.geeksforgeeks.org/user/your_username/' },
    { name: 'TryHackMe', subtext: 'Top 15% Global', icon: SiTryhackme, color: '#B30000', url: 'https://tryhackme.com/p/your_username' }
  ];

  const loopLogos = [
    { icon: FaReact, color: '#61DAFB' },
    { icon: FaJs, color: '#F7DF1E' },
    { icon: FaHtml5, color: '#E34F26' },
    { icon: FaCss3Alt, color: '#1572B6' },
    { icon: FaNodeJs, color: '#339933' },
    { icon: FaDatabase, color: '#47A248' },
    { icon: FaGit, color: '#F05032' },
    { icon: FaDocker, color: '#2496ED' },
    { icon: FaFigma, color: '#F24E1E' },
    { icon: FaPython, color: '#3776AB' },
    { icon: FaJava, color: '#ED8B00' },
    { icon: FaAws, color: '#FF9900' }
  ];

  const LogoTicker = ({ items }) => {
    const trackRef = useRef(null);
    const groupRef = useRef(null);

    useEffect(() => {
      let rafId = 0;
      let lastTs = 0;
      let offsetPx = 0;
      const speedPxPerSec = 80; // adjust speed here

      const step = (ts) => {
        if (!lastTs) lastTs = ts;
        const dt = (ts - lastTs) / 1000;
        lastTs = ts;
        const groupWidth = groupRef.current?.offsetWidth || 0;
        if (groupWidth > 0) {
          offsetPx = (offsetPx + speedPxPerSec * dt) % groupWidth;
          if (trackRef.current) {
            trackRef.current.style.transform = `translateX(-${offsetPx}px)`;
          }
        }
        rafId = requestAnimationFrame(step);
      };

      rafId = requestAnimationFrame(step);
      return () => cancelAnimationFrame(rafId);
    }, []);

    return (
      <section className="logo-ticker" aria-label="Technology logos">
        <div className="ticker-viewport">
          <div className="ticker-track" ref={trackRef}>
            <div className="ticker-group" ref={groupRef}>
              {items.map((item, idx) => {
                const IconComp = item.icon;
                return (
                  <span key={`g1-${idx}`} className="ticker-item" style={{ color: item.color }}>
                    <IconComp />
                  </span>
                );
              })}
            </div>
            <div className="ticker-group">
              {items.map((item, idx) => {
                const IconComp = item.icon;
                return (
                  <span key={`g2-${idx}`} className="ticker-item" style={{ color: item.color }}>
                    <IconComp />
                  </span>
                );
              })}
            </div>
            <div className="ticker-group">
              {items.map((item, idx) => {
                const IconComp = item.icon;
                return (
                  <span key={`g3-${idx}`} className="ticker-item" style={{ color: item.color }}>
                    <IconComp />
                  </span>
                );
              })}
            </div>
          </div>
        </div>
      </section>
    );
  };

  useEffect(() => {
    setIsVisible(true);
  }, []);

  const getSkillLevel = (level) => {
    if (level >= 90) return 'Expert';
    if (level >= 80) return 'Advanced';
    if (level >= 70) return 'Intermediate';
    if (level >= 60) return 'Beginner';
    return 'Learning';
  };

  const getSkillColor = (level) => {
    if (level >= 90) return '#10B981';
    if (level >= 80) return '#3B82F6';
    if (level >= 70) return '#F59E0B';
    if (level >= 60) return '#EF4444';
    return '#6B7280';
  };

  const SkillProgress = ({ level }) => {
    const [value, setValue] = useState(0);
    const containerRef = useRef(null);
    const startedRef = useRef(false);

    useEffect(() => {
      const node = containerRef.current;
      if (!node) return;

      const durationMs = 1200;
      let rafId = 0;
      let startTime = 0;

      const animate = (ts) => {
        if (!startTime) startTime = ts;
        const elapsed = ts - startTime;
        const progress = Math.min(elapsed / durationMs, 1);
        const eased = 1 - Math.pow(1 - progress, 3);
        setValue(level * eased);
        if (progress < 1) {
          rafId = requestAnimationFrame(animate);
        }
      };

      const observer = new IntersectionObserver(
        ([entry]) => {
          if (entry.isIntersecting && !startedRef.current) {
            startedRef.current = true;
            rafId = requestAnimationFrame(animate);
          }
        },
        { threshold: 0.35 }
      );

      observer.observe(node);
      return () => {
        observer.disconnect();
        if (rafId) cancelAnimationFrame(rafId);
      };
    }, [level]);

    const rounded = Math.round(value);
    return (
      <div ref={containerRef} className="skill-progress">
        <div className="progress-bar">
          <div
            className="progress-fill"
            style={{
              width: `${rounded}%`,
              backgroundColor: getSkillColor(level)
            }}
          ></div>
        </div>
        <span className="progress-text">{rounded}%</span>
      </div>
    );
  };

  return (
    <div className="skills-container">
      <section className="skills-hero">
        <div className="skills-content">
          <h1 className="skills-title">Skills & Expertise</h1>
          <p className="skills-subtitle">
            A comprehensive overview of my technical skills, design capabilities, 
            and the tools I use to bring ideas to life
          </p>
        </div>
      </section>

      <section className="skills-section">
        <div className="skills-categories">
          {skillCategories.map((category) => {
            const Icon = category.icon;
            return (
              <button
                key={category.id}
                className={`category-button ${activeCategory === category.id ? 'active' : ''}`}
                onClick={() => setActiveCategory(category.id)}
              >
                <Icon />
                <span>{category.label}</span>
              </button>
            );
          })}
        </div>

        {activeCategory !== 'profiles' ? (
          <div className="skills-grid">
            {skills[activeCategory].map((skill, index) => (
              <div
                key={skill.name}
                className="skill-card"
                style={{ animationDelay: `${index * 0.1}s` }}
              >
                <div className="skill-header">
                  <div 
                    className="skill-icon"
                    style={{ backgroundColor: `${skill.color}20`, color: skill.color }}
                  >
                    <skill.icon />
                  </div>
                  <div className="skill-info">
                    <h3 className="skill-name">{skill.name}</h3>
                    <span className="skill-level">{getSkillLevel(skill.level)}</span>
                  </div>
                </div>
                <SkillProgress level={skill.level} />
              </div>
            ))}
          </div>
        ) : (
          <div className="skills-grid">
            {codingProfiles.map((p, index) => (
              <a
                key={p.name}
                href={p.url}
                target="_blank"
                rel="noopener noreferrer"
                className="card-link"
                aria-label={`Open ${p.name} profile`}
                style={{ animationDelay: `${index * 0.1}s` }}
              >
                <div className="skill-card">
                  <div className="skill-header">
                    <div 
                      className="skill-icon"
                      style={{ backgroundColor: `${p.color}20`, color: p.color }}
                    >
                      <p.icon />
                    </div>
                    <div className="skill-info">
                      <h3 className="skill-name">{p.name}</h3>
                      <span className="profile-subtext">{p.subtext}</span>
                    </div>
                  </div>
                </div>
              </a>
            ))}
          </div>
        )}
      </section>

      <section className="skills-overview">
        <div className="overview-content">
          <h2>Why These Skills Matter</h2>
          <div className="overview-grid">
            <div className="overview-card">
              <div className="overview-icon">
                <FaCode />
              </div>
              <h3>Technical Excellence</h3>
              <p>
                Strong foundation in modern web technologies allows me to build 
                robust, scalable applications that perform exceptionally well.
              </p>
            </div>
            
            <div className="overview-card">
              <div className="overview-icon">
                <FaPalette />
              </div>
              <h3>Design Thinking</h3>
              <p>
                Understanding of design principles helps me create intuitive 
                user experiences that are both beautiful and functional.
              </p>
            </div>
            
            <div className="overview-card">
              <div className="overview-icon">
                <FaRocket />
              </div>
              <h3>Problem Solving</h3>
              <p>
                Experience with various tools and technologies enables me to 
                find the best solutions for complex technical challenges.
              </p>
            </div>
            
            <div className="overview-card">
              <div className="overview-icon">
                <FaUsers />
              </div>
              <h3>Collaboration</h3>
              <p>
                Proficiency in development tools and practices ensures smooth 
                collaboration with teams and efficient project delivery.
              </p>
            </div>
          </div>
        </div>
      </section>

      <LogoTicker items={loopLogos} />
    </div>
  );
}
